// dropdown-menu.scss

.dropdown-menu {
  width: 200px;
  font-size: 1rem;

  .dropdown-menu-activator {
    padding: 1em;
    font-size: inherit;
    font-weight: bold;    
  }

  [role=menu] {
    list-style: none;
    padding: 0;
    margin: 0;
    display: none;

    &[aria-expanded=true] {
      display: block;
    }
  }

  [role=menuitem]  {
    border: 1px solid #ddd;
    border-bottom: none;

    &:last-child {
      border-bottom: 1px solid #ddd;
    }   

    button {
      background: none;
      border: none;
      padding: 1em;
      display: block;
      width: 100%;
      text-align: left;
      font-size: inherit;

      &:hover, &:focus {
        background: skyblue;
      }
    }
  }
}

